import React, { useEffect, useState } from 'react';
import StatsTable from '../components/StatsTable';
import { getStats, buildRedirectUrl } from '../services/api';

const SESSION_KEY = 'createdShortUrls_v1';

export default function StatsPage() {
  const [sessionItems, setSessionItems] = useState([]);
  const [selectedCode, setSelectedCode] = useState('');
  const [statsItems, setStatsItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    try {
      const raw = sessionStorage.getItem(SESSION_KEY);
      if (raw) {
        const parsed = JSON.parse(raw);
        setSessionItems(parsed);
      }
    } catch (err) {
      console.warn('Failed to load session items', err);
    }
  }, []);

  const handleFetchStats = async (code) => {
    setError('');
    setLoading(true);
    setStatsItems([]);
    try {
      const data = await getStats(code);
      // normalize into StatsTable item shape
      const item = {
        shortcode: code,
        originalUrl: data.originalUrl || data.original || data.url,
        createdAt: data.createdAt || data.created,
        expiryAt: data.expiry || data.expiresAt,
        totalClicks: data.clicks ?? data.totalClicks ?? 0,
        clickEvents: data.clickEvents || data.events || data.hits || []
      };
      setStatsItems([item]);
    } catch (err) {
      const msg = err.response?.data?.error || err.message || 'Failed to fetch stats';
      setError(String(msg));
    } finally {
      setLoading(false);
    }
  };

  const handleSelectSession = (e) => {
    const code = e.target.value;
    setSelectedCode(code);
    if (code) {
      handleFetchStats(code);
    } else {
      setStatsItems([]);
    }
  };

  const handleManualSubmit = (ev) => {
    ev.preventDefault();
    if (selectedCode) handleFetchStats(selectedCode);
  };

  return (
    <div>
      <h2>Statistics</h2>
      <p>Use the UI to view individual short URL stats. You can select one of the shortcodes created in this session or paste a shortcode below.</p>

      <div style={{ marginBottom: 12 }}>
        <label style={{ display: 'block', marginBottom: 6 }}>Shortcodes created in this session</label>
        <select value={selectedCode} onChange={handleSelectSession} style={{ padding: 8, width: 320 }}>
          <option value="">-- choose one --</option>
          {sessionItems.map((it, idx) => {
            const code = it.shortcode || it.shortLink || it.code || it.shortCode;
            const label = code || (it.shortLink ? it.shortLink : `item ${idx + 1}`);
            return <option key={idx} value={code}>{label}</option>;
          })}
        </select>
      </div>

      <form onSubmit={handleManualSubmit} style={{ marginBottom: 12 }}>
        <label style={{ display: 'block', marginBottom: 6 }}>Or paste a shortcode</label>
        <input
          type="text"
          value={selectedCode}
          onChange={(e) => setSelectedCode(e.target.value)}
          placeholder="e.g. abcd"
          style={{ padding: 8, width: 320, marginRight: 8 }}
        />
        <button type="submit" disabled={loading} style={{ padding: '8px 12px' }}>
          {loading ? 'Loading...' : 'Get stats'}
        </button>
      </form>

      {error && <div style={{ color: 'red', marginBottom: 8 }}>{error}</div>}

      <StatsTable items={statsItems} />
    </div>
  );
}
