import React, { useEffect, useState } from 'react';
import UrlForm from '../components/UrlForm';
import UrlList from '../components/UrlList';

/**
 * Page that allows up to 5 concurrent entries.
 * The heavy lifting (shortening) MUST be done by backend API.
 *
 * This page persists created items into sessionStorage so Stats page can access them.
 */

const SESSION_KEY = 'createdShortUrls_v1';

export default function ShortenerPage() {
  const [results, setResults] = useState([]); // array of created objects

  useEffect(() => {
    // load saved session items
    try {
      const raw = sessionStorage.getItem(SESSION_KEY);
      if (raw) setResults(JSON.parse(raw));
    } catch (err) {
      console.warn('Failed to load session items', err);
    }
  }, []);

  const handleSuccess = (resObjects) => {
    // resObjects may be array or single object; normalize
    const newItems = Array.isArray(resObjects) ? resObjects : [resObjects];
    // merge with existing, keep last 10
    const merged = [...newItems, ...results].slice(0, 10); // keep up to 10
    setResults(merged);
    try {
      sessionStorage.setItem(SESSION_KEY, JSON.stringify(merged));
    } catch (err) {
      console.warn('Failed to save session items', err);
    }
  };

  return (
    <div>
      <h2>URL Shortener</h2>
      <UrlForm onSuccess={handleSuccess} />
      <UrlList items={results} />
    </div>
  );
}
